styles.variables:
:root[data-theme-id="dark-green-theme-modernized"] {
  /* 墨绿深邃主题变量 */
  --theme-primary: #D4AF37;
  --theme-primary-hover: #B8941F;
  --theme-primary-active: #9C7A12;
  --theme-background: #0D1B0D;
  --theme-background-secondary: #132B13;
  --theme-background-tertiary: #1A3B1A;
  --theme-surface: #132B13;
  --theme-surface-hover: #1A3B1A;
  --theme-surface-selected: #214B21;
  --theme-text-primary: #F0F8F0;
  --theme-text-secondary: #D0E0D0;
  --theme-text-tertiary: #A0C0A0;
  --theme-text-disabled: #708070;
  --theme-border: #2D4B2D;
  --theme-border-hover: #3D5B3D;
  --theme-border-active: #D4AF37;
  --theme-success: #4CAF50;
  --theme-warning: #FFA726;
  --theme-error: #EF5350;
  --theme-info: #42A5F5;
  --theme-sidebar-background: #0D1B0D;
  --theme-sidebar-hover: #1A3B1A;
  --theme-logo-background: #D4AF37;
  --theme-logo-foreground: #0D1B0D;
  --theme-search-highlight: #FFE4B5;
  --theme-search-highlight-background: #2D4B2D;
  --theme-icon-primary: #B8C8B8;
  --theme-icon-secondary: #8B9B8B;

  /* 语义化变量 */
  --theme-text-on-primary: var(--theme-logo-foreground);
  --theme-surface-primary: var(--theme-surface);
  --theme-surface-secondary: var(--theme-surface-hover);

  /* 组件特定变量 */
  --theme-button-radius: 6px;
  --theme-input-radius: 6px;
  --theme-card-radius: 8px;
  --theme-modal-radius: 8px;

  /* 间距变量 */
  --theme-spacing-xs: 4px;
  --theme-spacing-sm: 8px;
  --theme-spacing-md: 16px;
  --theme-spacing-lg: 24px;
  --theme-spacing-xl: 32px;

  /* 阴影变量 */
  --theme-shadow-sm: 0 1px 2px rgba(0, 0, 0, 0.3);
  --theme-shadow-md: 0 4px 6px rgba(0, 0, 0, 0.3);
  --theme-shadow-lg: 0 10px 15px rgba(0, 0, 0, 0.3);
}

components.button:
[data-theme-id="dark-green-theme-modernized"] .aion-button {
  background-color: var(--theme-primary);
  color: var(--theme-text-on-primary);
  border: 1px solid var(--theme-primary);
  border-radius: var(--theme-button-radius);
  padding: var(--theme-spacing-sm) var(--theme-spacing-md);
  font-size: 14px;
  cursor: pointer;
  transition: all 0.2s ease;
}

[data-theme-id="dark-green-theme-modernized"] .aion-button:hover {
  background-color: var(--theme-primary-hover);
  border-color: var(--theme-primary-hover);
}

[data-theme-id="dark-green-theme-modernized"] .aion-button--secondary {
  background-color: transparent;
  color: var(--theme-text-primary);
  border-color: var(--theme-border);
}

[data-theme-id="dark-green-theme-modernized"] .aion-button--secondary:hover {
  background-color: var(--theme-surface-hover);
  border-color: var(--theme-border-hover);
}

components.form:
[data-theme-id="dark-green-theme-modernized"] .aion-input {
  background-color: var(--theme-surface);
  border: 1px solid var(--theme-border);
  border-radius: var(--theme-input-radius);
  color: var(--theme-text-primary);
  padding: var(--theme-spacing-sm);
  font-size: 14px;
  transition: all 0.2s ease;
}

[data-theme-id="dark-green-theme-modernized"] .aion-input:focus {
  outline: none;
  border-color: var(--theme-primary);
  box-shadow: 0 0 0 2px rgba(212, 175, 55, 0.2);
}

[data-theme-id="dark-green-theme-modernized"] .aion-input::placeholder {
  color: var(--theme-text-tertiary);
}

components.dropdown:
[data-theme-id="dark-green-theme-modernized"] .aion-dropdown {
  background-color: var(--theme-surface);
  border: 1px solid var(--theme-border);
  border-radius: var(--theme-input-radius);
  box-shadow: var(--theme-shadow-lg);
  padding: var(--theme-spacing-xs) 0;
  min-width: 160px;
}

[data-theme-id="dark-green-theme-modernized"] .aion-dropdown-item {
  padding: var(--theme-spacing-sm) var(--theme-spacing-md);
  color: var(--theme-text-primary);
  cursor: pointer;
  transition: background-color 0.2s ease;
}

[data-theme-id="dark-green-theme-modernized"] .aion-dropdown-item:hover {
  background-color: var(--theme-surface-hover);
}

components.modal:
[data-theme-id="dark-green-theme-modernized"] .aion-modal {
  background-color: var(--theme-surface);
  border-radius: var(--theme-modal-radius);
  box-shadow: var(--theme-shadow-lg);
  border: 1px solid var(--theme-border);
  max-width: 600px;
  width: 90vw;
}

[data-theme-id="dark-green-theme-modernized"] .aion-modal-header {
  padding: var(--theme-spacing-lg);
  border-bottom: 1px solid var(--theme-border);
}

[data-theme-id="dark-green-theme-modernized"] .aion-modal-title {
  color: var(--theme-text-primary);
  font-size: 18px;
  font-weight: 600;
  margin: 0;
}

[data-theme-id="dark-green-theme-modernized"] .aion-modal-body {
  padding: var(--theme-spacing-lg);
  color: var(--theme-text-primary);
}

layouts.sidebar:
[data-theme-id="dark-green-theme-modernized"] .theme-sidebar {
  background-color: var(--theme-sidebar-background);
  border-right: 1px solid var(--theme-border);
  color: var(--theme-text-primary);
}

[data-theme-id="dark-green-theme-modernized"] .sidebar-item {
  padding: var(--theme-spacing-sm) var(--theme-spacing-md);
  color: var(--theme-text-primary);
  transition: background-color 0.2s ease;
  cursor: pointer;
}

[data-theme-id="dark-green-theme-modernized"] .sidebar-item:hover {
  background-color: var(--theme-sidebar-hover);
}

[data-theme-id="dark-green-theme-modernized"] .sidebar-item--active {
  background-color: var(--theme-surface-selected);
  color: var(--theme-primary);
}

layouts.main:
[data-theme-id="dark-green-theme-modernized"] .theme-main-content {
  background-color: var(--theme-background);
  color: var(--theme-text-primary);
  min-height: 100vh;
  flex: 1;
}

[data-theme-id="dark-green-theme-modernized"] .theme-layout {
  background-color: var(--theme-background);
  color: var(--theme-text-primary);
  min-height: 100vh;
}

styles.overrides:
/* 墨绿深邃主题 - Arco Design 组件覆盖 */
[data-theme-id="dark-green-theme-modernized"] .arco-btn {
  border-radius: var(--theme-button-radius);
  transition: all 0.2s ease;
}

[data-theme-id="dark-green-theme-modernized"] .arco-btn-primary {
  background-color: var(--theme-primary);
  border-color: var(--theme-primary);
  color: var(--theme-text-on-primary);
}

[data-theme-id="dark-green-theme-modernized"] .arco-btn-primary:hover {
  background-color: var(--theme-primary-hover);
  border-color: var(--theme-primary-hover);
}

[data-theme-id="dark-green-theme-modernized"] .arco-input {
  background-color: var(--theme-surface);
  border-color: var(--theme-border);
  color: var(--theme-text-primary);
  border-radius: var(--theme-input-radius);
}

[data-theme-id="dark-green-theme-modernized"] .arco-input:focus {
  border-color: var(--theme-primary);
  box-shadow: 0 0 0 2px rgba(212, 175, 55, 0.2);
}

[data-theme-id="dark-green-theme-modernized"] .arco-card {
  background-color: var(--theme-surface);
  border-color: var(--theme-border);
  color: var(--theme-text-primary);
  border-radius: var(--theme-card-radius);
}

[data-theme-id="dark-green-theme-modernized"] .arco-modal {
  background-color: var(--theme-surface);
  border-radius: var(--theme-modal-radius);
  box-shadow: var(--theme-shadow-lg);
}
