styles.variables:
:root[data-theme-id="default-light-modernized"] {
  /* Default Light 主题变量 */
  --theme-primary: #4E5969;
  --theme-primary-hover: #3D4652;
  --theme-primary-active: #2A2E37;
  --theme-background: #ffffff;
  --theme-background-secondary: #f7f8fa;
  --theme-background-tertiary: #f2f3f5;
  --theme-surface: #ffffff;
  --theme-surface-hover: #f7f8fa;
  --theme-surface-selected: #e5e6eb;
  --theme-text-primary: #1d2129;
  --theme-text-secondary: #4e5969;
  --theme-text-tertiary: #86909c;
  --theme-text-disabled: #c9cdd4;
  --theme-border: #e5e6eb;
  --theme-border-hover: #c9cdd4;
  --theme-border-active: #86909c;
  --theme-success: #00b42a;
  --theme-warning: #ff7d00;
  --theme-error: #f53f3f;
  --theme-info: #165dff;
  --theme-sidebar-background: #f2f3f5;
  --theme-sidebar-hover: #e5e6eb;
  --theme-logo-background: #000000;
  --theme-logo-foreground: #ffffff;
  --theme-search-highlight: #ff6b35;
  --theme-search-highlight-background: #fff3e0;
  --theme-icon-primary: #4e5969;
  --theme-icon-secondary: #86909c;

  /* 语义化变量 */
  --theme-text-on-primary: #ffffff;
  --theme-surface-primary: var(--theme-surface);
  --theme-surface-secondary: var(--theme-surface-hover);

  /* 组件特定变量 */
  --theme-button-radius: 6px;
  --theme-input-radius: 6px;
  --theme-card-radius: 8px;
  --theme-modal-radius: 8px;

  /* 间距变量 */
  --theme-spacing-xs: 4px;
  --theme-spacing-sm: 8px;
  --theme-spacing-md: 16px;
  --theme-spacing-lg: 24px;
  --theme-spacing-xl: 32px;

  /* 阴影变量 - 浅色主题 */
  --theme-shadow-sm: 0 1px 2px rgba(0, 0, 0, 0.05);
  --theme-shadow-md: 0 4px 6px rgba(0, 0, 0, 0.1);
  --theme-shadow-lg: 0 10px 15px rgba(0, 0, 0, 0.1);
}

components.button:
[data-theme-id="default-light-modernized"] .aion-button {
  background-color: var(--theme-primary);
  color: var(--theme-text-on-primary);
  border: 1px solid var(--theme-primary);
  border-radius: var(--theme-button-radius);
  padding: var(--theme-spacing-sm) var(--theme-spacing-md);
  font-size: 14px;
  cursor: pointer;
  transition: all 0.2s ease;
}

[data-theme-id="default-light-modernized"] .aion-button:hover {
  background-color: var(--theme-primary-hover);
  border-color: var(--theme-primary-hover);
}

[data-theme-id="default-light-modernized"] .aion-button--secondary {
  background-color: transparent;
  color: var(--theme-text-primary);
  border-color: var(--theme-border);
}

[data-theme-id="default-light-modernized"] .aion-button--secondary:hover {
  background-color: var(--theme-surface-hover);
  border-color: var(--theme-border-hover);
}

components.form:
[data-theme-id="default-light-modernized"] .aion-input {
  background-color: var(--theme-surface);
  border: 1px solid var(--theme-border);
  border-radius: var(--theme-input-radius);
  color: var(--theme-text-primary);
  padding: var(--theme-spacing-sm);
  font-size: 14px;
  transition: all 0.2s ease;
}

[data-theme-id="default-light-modernized"] .aion-input:focus {
  outline: none;
  border-color: var(--theme-primary);
  box-shadow: 0 0 0 2px rgba(78, 89, 105, 0.2);
}

[data-theme-id="default-light-modernized"] .aion-input::placeholder {
  color: var(--theme-text-tertiary);
}

components.dropdown:
[data-theme-id="default-light-modernized"] .aion-dropdown {
  background-color: var(--theme-surface);
  border: 1px solid var(--theme-border);
  border-radius: var(--theme-input-radius);
  box-shadow: var(--theme-shadow-lg);
  padding: var(--theme-spacing-xs) 0;
  min-width: 160px;
}

[data-theme-id="default-light-modernized"] .aion-dropdown-item {
  padding: var(--theme-spacing-sm) var(--theme-spacing-md);
  color: var(--theme-text-primary);
  cursor: pointer;
  transition: background-color 0.2s ease;
}

[data-theme-id="default-light-modernized"] .aion-dropdown-item:hover {
  background-color: var(--theme-surface-hover);
}

components.modal:
[data-theme-id="default-light-modernized"] .aion-modal {
  background-color: var(--theme-surface);
  border-radius: var(--theme-modal-radius);
  box-shadow: var(--theme-shadow-lg);
  border: 1px solid var(--theme-border);
  max-width: 600px;
  width: 90vw;
}

[data-theme-id="default-light-modernized"] .aion-modal-header {
  padding: var(--theme-spacing-lg);
  border-bottom: 1px solid var(--theme-border);
}

[data-theme-id="default-light-modernized"] .aion-modal-title {
  color: var(--theme-text-primary);
  font-size: 18px;
  font-weight: 600;
  margin: 0;
}

[data-theme-id="default-light-modernized"] .aion-modal-body {
  padding: var(--theme-spacing-lg);
  color: var(--theme-text-primary);
}

layouts.sidebar:
[data-theme-id="default-light-modernized"] .theme-sidebar {
  background-color: var(--theme-sidebar-background);
  border-right: 1px solid var(--theme-border);
  color: var(--theme-text-primary);
}

[data-theme-id="default-light-modernized"] .sidebar-item {
  padding: var(--theme-spacing-sm) var(--theme-spacing-md);
  color: var(--theme-text-primary);
  transition: background-color 0.2s ease;
  cursor: pointer;
}

[data-theme-id="default-light-modernized"] .sidebar-item:hover {
  background-color: var(--theme-sidebar-hover);
}

[data-theme-id="default-light-modernized"] .sidebar-item--active {
  background-color: var(--theme-surface-selected);
  color: var(--theme-primary);
}

layouts.main:
[data-theme-id="default-light-modernized"] .theme-main-content {
  background-color: var(--theme-background);
  color: var(--theme-text-primary);
  min-height: 100vh;
  flex: 1;
}

[data-theme-id="default-light-modernized"] .theme-layout {
  background-color: var(--theme-background);
  color: var(--theme-text-primary);
  min-height: 100vh;
}

styles.overrides:
/* Default Light - Arco Design 组件覆盖 */
[data-theme-id="default-light-modernized"] .arco-btn {
  border-radius: var(--theme-button-radius);
  transition: all 0.2s ease;
}

[data-theme-id="default-light-modernized"] .arco-btn-primary {
  background-color: var(--theme-primary);
  border-color: var(--theme-primary);
  color: var(--theme-text-on-primary);
}

[data-theme-id="default-light-modernized"] .arco-btn-primary:hover {
  background-color: var(--theme-primary-hover);
  border-color: var(--theme-primary-hover);
}

[data-theme-id="default-light-modernized"] .arco-input {
  background-color: var(--theme-surface);
  border-color: var(--theme-border);
  color: var(--theme-text-primary);
  border-radius: var(--theme-input-radius);
}

[data-theme-id="default-light-modernized"] .arco-input:focus {
  border-color: var(--theme-primary);
  box-shadow: 0 0 0 2px rgba(78, 89, 105, 0.2);
}

[data-theme-id="default-light-modernized"] .arco-card {
  background-color: var(--theme-surface);
  border-color: var(--theme-border);
  color: var(--theme-text-primary);
  border-radius: var(--theme-card-radius);
}

[data-theme-id="default-light-modernized"] .arco-modal {
  background-color: var(--theme-surface);
  border-radius: var(--theme-modal-radius);
  box-shadow: var(--theme-shadow-lg);
}
