styles.variables:
:root[data-theme-id="idea-dark-modernized"] {
  /* IDEA Dark 主题变量 */
  --theme-primary: #6494ED;
  --theme-primary-hover: #5B85D6;
  --theme-primary-active: #4A75C2;
  --theme-background: #1E1E1E;
  --theme-background-secondary: #252526;
  --theme-background-tertiary: #2D2D30;
  --theme-surface: #252526;
  --theme-surface-hover: #2D2D30;
  --theme-surface-selected: #37373D;
  --theme-text-primary: #E8E8E8;
  --theme-text-secondary: #C0C0C0;
  --theme-text-tertiary: #909090;
  --theme-text-disabled: #606060;
  --theme-border: #3E3E42;
  --theme-border-hover: #464647;
  --theme-border-active: #6494ED;
  --theme-success: #6A8759;
  --theme-warning: #BBB529;
  --theme-error: #BC3F3C;
  --theme-info: #6897BB;
  --theme-sidebar-background: #1E1E1E;
  --theme-sidebar-hover: #2D2D30;
  --theme-logo-background: #6494ED;
  --theme-logo-foreground: #FFFFFF;
  --theme-search-highlight: #FFD700;
  --theme-search-highlight-background: #3D3D00;
  --theme-icon-primary: #B0B0B0;
  --theme-icon-secondary: #808080;

  /* 语义化变量 */
  --theme-text-on-primary: #ffffff;
  --theme-surface-primary: var(--theme-surface);
  --theme-surface-secondary: var(--theme-surface-hover);

  /* 组件特定变量 */
  --theme-button-radius: 6px;
  --theme-input-radius: 6px;
  --theme-card-radius: 8px;
  --theme-modal-radius: 8px;

  /* 间距变量 */
  --theme-spacing-xs: 4px;
  --theme-spacing-sm: 8px;
  --theme-spacing-md: 16px;
  --theme-spacing-lg: 24px;
  --theme-spacing-xl: 32px;

  /* 阴影变量 - 暗色主题 */
  --theme-shadow-sm: 0 1px 2px rgba(0, 0, 0, 0.3);
  --theme-shadow-md: 0 4px 6px rgba(0, 0, 0, 0.3);
  --theme-shadow-lg: 0 10px 15px rgba(0, 0, 0, 0.3);
}

components.button:
[data-theme-id="idea-dark-modernized"] .aion-button {
  background-color: var(--theme-primary);
  color: var(--theme-text-on-primary);
  border: 1px solid var(--theme-primary);
  border-radius: var(--theme-button-radius);
  padding: var(--theme-spacing-sm) var(--theme-spacing-md);
  font-size: 14px;
  cursor: pointer;
  transition: all 0.2s ease;
}

[data-theme-id="idea-dark-modernized"] .aion-button:hover {
  background-color: var(--theme-primary-hover);
  border-color: var(--theme-primary-hover);
}

[data-theme-id="idea-dark-modernized"] .aion-button--secondary {
  background-color: transparent;
  color: var(--theme-text-primary);
  border-color: var(--theme-border);
}

[data-theme-id="idea-dark-modernized"] .aion-button--secondary:hover {
  background-color: var(--theme-surface-hover);
  border-color: var(--theme-border-hover);
}

components.form:
[data-theme-id="idea-dark-modernized"] .aion-input {
  background-color: var(--theme-surface);
  border: 1px solid var(--theme-border);
  border-radius: var(--theme-input-radius);
  color: var(--theme-text-primary);
  padding: var(--theme-spacing-sm);
  font-size: 14px;
  transition: all 0.2s ease;
}

[data-theme-id="idea-dark-modernized"] .aion-input:focus {
  outline: none;
  border-color: var(--theme-primary);
  box-shadow: 0 0 0 2px rgba(100, 148, 237, 0.2);
}

[data-theme-id="idea-dark-modernized"] .aion-input::placeholder {
  color: var(--theme-text-tertiary);
}

components.dropdown:
[data-theme-id="idea-dark-modernized"] .aion-dropdown {
  background-color: var(--theme-surface);
  border: 1px solid var(--theme-border);
  border-radius: var(--theme-input-radius);
  box-shadow: var(--theme-shadow-lg);
  padding: var(--theme-spacing-xs) 0;
  min-width: 160px;
}

[data-theme-id="idea-dark-modernized"] .aion-dropdown-item {
  padding: var(--theme-spacing-sm) var(--theme-spacing-md);
  color: var(--theme-text-primary);
  cursor: pointer;
  transition: background-color 0.2s ease;
}

[data-theme-id="idea-dark-modernized"] .aion-dropdown-item:hover {
  background-color: var(--theme-surface-hover);
}

components.modal:
[data-theme-id="idea-dark-modernized"] .aion-modal {
  background-color: var(--theme-surface);
  border-radius: var(--theme-modal-radius);
  box-shadow: var(--theme-shadow-lg);
  border: 1px solid var(--theme-border);
  max-width: 600px;
  width: 90vw;
}

[data-theme-id="idea-dark-modernized"] .aion-modal-header {
  padding: var(--theme-spacing-lg);
  border-bottom: 1px solid var(--theme-border);
}

[data-theme-id="idea-dark-modernized"] .aion-modal-title {
  color: var(--theme-text-primary);
  font-size: 18px;
  font-weight: 600;
  margin: 0;
}

[data-theme-id="idea-dark-modernized"] .aion-modal-body {
  padding: var(--theme-spacing-lg);
  color: var(--theme-text-primary);
}

layouts.sidebar:
[data-theme-id="idea-dark-modernized"] .theme-sidebar {
  background-color: var(--theme-sidebar-background);
  border-right: 1px solid var(--theme-border);
  color: var(--theme-text-primary);
}

[data-theme-id="idea-dark-modernized"] .sidebar-item {
  padding: var(--theme-spacing-sm) var(--theme-spacing-md);
  color: var(--theme-text-primary);
  transition: background-color 0.2s ease;
  cursor: pointer;
}

[data-theme-id="idea-dark-modernized"] .sidebar-item:hover {
  background-color: var(--theme-sidebar-hover);
}

[data-theme-id="idea-dark-modernized"] .sidebar-item--active {
  background-color: var(--theme-surface-selected);
  color: var(--theme-primary);
}

layouts.main:
[data-theme-id="idea-dark-modernized"] .theme-main-content {
  background-color: var(--theme-background);
  color: var(--theme-text-primary);
  min-height: 100vh;
  flex: 1;
}

[data-theme-id="idea-dark-modernized"] .theme-layout {
  background-color: var(--theme-background);
  color: var(--theme-text-primary);
  min-height: 100vh;
}

styles.overrides:
/* IDEA Dark - Arco Design 组件覆盖 */
[data-theme-id="idea-dark-modernized"] .arco-btn {
  border-radius: var(--theme-button-radius);
  transition: all 0.2s ease;
}

[data-theme-id="idea-dark-modernized"] .arco-btn-primary {
  background-color: var(--theme-primary);
  border-color: var(--theme-primary);
  color: var(--theme-text-on-primary);
}

[data-theme-id="idea-dark-modernized"] .arco-btn-primary:hover {
  background-color: var(--theme-primary-hover);
  border-color: var(--theme-primary-hover);
}

[data-theme-id="idea-dark-modernized"] .arco-input {
  background-color: var(--theme-surface);
  border-color: var(--theme-border);
  color: var(--theme-text-primary);
  border-radius: var(--theme-input-radius);
}

[data-theme-id="idea-dark-modernized"] .arco-input:focus {
  border-color: var(--theme-primary);
  box-shadow: 0 0 0 2px rgba(100, 148, 237, 0.2);
}

[data-theme-id="idea-dark-modernized"] .arco-card {
  background-color: var(--theme-surface);
  border-color: var(--theme-border);
  color: var(--theme-text-primary);
  border-radius: var(--theme-card-radius);
}

[data-theme-id="idea-dark-modernized"] .arco-modal {
  background-color: var(--theme-surface);
  border-radius: var(--theme-modal-radius);
  box-shadow: var(--theme-shadow-lg);
}
